mapper.select:
SELECT -- 文章表数据 art.id, art.title, -- art.cover, -- art.digest, art.tags, art.gmt_create, -- 用户信息 u.id as authorId, u.nickname as authorName, u.avatar as authorAvatar, -- 浏览量 art.view_count, -- 文章评论表数据 COALESCE(com.count, 0) as comment_count, -- 点赞数量 COALESCE(lik.count, 0) as good_count, -- 收藏数量 COALESCE(col.count, 0) as collect_count FROM xy_article art -- 左拼接查询用户信息 LEFT JOIN sys_user u ON u.id = art.author_id -- 左拼接查询评论数量 LEFT JOIN (SELECT article_id, count(id) AS count FROM xy_article_comment GROUP BY article_id) com ON com.article_id = art.id -- 左拼接查询点赞数量 LEFT JOIN (SELECT article_id, count(id) AS count FROM xy_article_like GROUP BY article_id) lik ON lik.article_id = art.id -- 左拼接查询收藏数量 LEFT JOIN (SELECT article_id, count(id) AS count from xy_article_collect GROUP BY article_id) col ON col.article_id = art.id
art.deleted = '0'
AND art.id = #{param.id}
AND art.tags like concat(#{param.tag}, '%')
AND art.title like concat(#{param.title}, '%')
AND art.gmt_create >= #{param.startTime}
AND art.gmt_create <= #{param.endTime}
AND art.author_id = #{param.isAdmin}
ORDER BY ${param.sort} DESC
ORDER BY gmt_create DESC
